mapper.select:
select hour_date, ACT_RE_PROCDEF.KEY_ , count(*) as instanceCount FROM (select sysdate as hour_date union select DATEADD('DAY', -1*rownum, sysdate) as hour_date from SYSTEM_RANGE(1, 13)) join act_hi_procinst on (DAY_OF_YEAR(start_time_) = DAY_OF_YEAR(hour_date)) join ACT_RE_PROCDEF on act_hi_procinst.PROC_DEF_ID_ = ACT_RE_PROCDEF.ID_
WHERE ACT_RE_PROCDEF.KEY_ = #{parameter.processDefinitionKey}
group by (hour_date, ACT_RE_PROCDEF.KEY_ ) order by ACT_RE_PROCDEF.KEY_, hour_date
